import React, { useState, useEffect } from 'react';

function Education() {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('https://jsonplaceholder.typicode.com/posts/1')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((jsonData) => {
        setData(jsonData);
      })
      .catch((error) => {
        setError(error);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>Error: {error.message}</p>;
  }
data = {
    name: "Anmol Bhusal",
    date: "2021-09-21",
    about: "This is a project I did for my college. I used React.js for the frontend and Node.js for the backend. It was a great learning experience.",
    description: "This is a project I did for my college. I used React.js for the frontend and Node.js for the backend. It was a great learning experience.",
    file: "https://www.google.com"
}
  return (
    <div className='container'>
        <div className="d-flex flex-column user">
      <p className='h1 m-2'>{data.name}</p>
      <span>{data.date}</span>
        </div>
        <div className="details">
            <p>{data.about}</p>
            <p>{data.description}</p>
        </div>
        <div className="file">
            {data.file}
        </div>

    </div>
  );
}

export default Education;
